#!/bin/sh

COPYRIGHT="s#^// Copyright .*#// Copyright Joyent, Inc. and other Node contributors.#"
MODULE="s#'\./#'dns_#"

cat lib/client.js | sed -e $MODULE | sed -e "$COPYRIGHT" > ../node/lib/dns_client.js
cat lib/consts.js | sed -e $MODULE | sed -e "$COPYRIGHT" > ../node/lib/dns_consts.js
cat lib/fields.js | sed -e $MODULE | sed -e "$COPYRIGHT" > ../node/lib/dns_fields.js
cat lib/message.js | sed -e $MODULE | sed -e "$COPYRIGHT" > ../node/lib/dns_message.js
cat lib/packet.js | sed -e $MODULE | sed -e "$COPYRIGHT" > ../node/lib/dns_packet.js
cat lib/pending.js | sed -e $MODULE | sed -e "$COPYRIGHT" > ../node/lib/dns_pending.js
cat lib/platform.js | sed -e $MODULE | sed -e "$COPYRIGHT" > ../node/lib/dns_platform.js
cat lib/resourcerecord.js | sed -e $MODULE | sed -e "$COPYRIGHT" > ../node/lib/dns_resourcerecord.js
cat lib/server.js | sed -e $MODULE | sed -e "$COPYRIGHT" > ../node/lib/dns_server.js
cat lib/types.js | sed -e $MODULE | sed -e "$COPYRIGHT" > ../node/lib/dns_types.js
cat lib/utils.js | sed -e $MODULE | sed -e "$COPYRIGHT" > ../node/lib/dns_utils.js
cp lib/ipaddr.js ../node/lib/dns_ipaddr.js
cat dns.js | sed -e s#'\./lib/#'dns_# | sed -e "$COPYRIGHT" > ../node/lib/dns.js
